      // event.preventDefault(); //event.preventDefault bei Submit behebt den fehler, eite wird nicht mehr selbstständig geladen
       const s = this.state.value.split(/(\d+)/);
       let anzahl = s[s.length - 2];
       console.log("split: " + anzahl);
       console.log("ar: " + s);
