           s.length = s.length - 2;
       } else {
           anzahl = 1;
       }

       console.log("sL: " + s.length + "s: " +s);
        const p = s.toString();

   console.log("target: " + event.target[0].value);
       axios({
      method: 'post',
      url: 'http://127.0.0.1:8081/einkaufsListe',
      data: {
        "itId": 100,
        "einkaufsPunkt": p,
