      },
    })
  };

    handleNumber(a,e){
        this.setState({amount: e.target.value});
        console.log("nummber: " +e.target.value);
        e.preventDefault();
        axios({
            method: 'put',
            url: 'http://127.0.0.1:8081/einkaufsListeAnzahlAendern',
            data: {
                "itId": a,
                "einkaufsPunkt": "platzhalterdatenloeschen",
                "strich": false,
                "amount": e.target.value
            },
        })
    }

    handleButton(a,e){
        console.log(a);
